//go:build !ignore_autogenerated

package test

import (
	"github.com/DougTea/go-common/pkg/web"
	"github.com/gin-gonic/gin"
)

type TestServiceController struct {
	Service TestService
	Routers []*web.Router
}

func NewTestServiceController(svc TestService) *TestServiceController {
	return &TestServiceController{
		Service: svc,
		Routers: []*web.Router{
			newGetParmRouter(svc),
			newGetParmAndResRouter(svc),
			newPostResRouter(svc),
		},
	}
}

func newGetParmRouter(svc TestService) *web.Router {
	return &web.Router{
		Method: web.MethodGet,
		Path:   "/test/param",
		Handler: func(c *gin.Context) {
			p := new(Param)
			err := c.Bind(p)
			if err != nil {
				c.Error(err)
				return
			}
			err = svc.GetParm(*p)
			if err != nil {
				c.Error(err)
			} else {
				c.JSON(201, nil)
			}
		},
	}
}

func newGetParmAndResRouter(svc TestService) *web.Router {
	return &web.Router{
		Method: web.MethodPost,
		Path:   "/test/paramAndRes",
		Handler: func(c *gin.Context) {
			p := new(Param)
			err := c.Bind(p)
			if err != nil {
				c.Error(err)
				return
			}
			r, err := svc.GetParmAndRes(p)
			if err != nil {
				c.Error(err)
			} else {
				c.JSON(201, r)
			}
		},
	}
}

func newPostResRouter(svc TestService) *web.Router {
	return &web.Router{
		Method: web.MethodPost,
		Path:   "/test/res",
		Handler: func(c *gin.Context) {
			err := svc.PostRes()
			if err != nil {
				c.Error(err)
			} else {
				c.JSON(201, nil)
			}
		},
	}
}
